class a{constructor(s=[]){this.segments=s,this.transform=[0,0]}moveTo(s,e){this.segments.push([[s,e]])}lineTo(s,e){this.segments.at(-1).push([s,e])}translate(s,e){return new a(this.segments.map(t=>t.map(([r,n])=>[r+s,n+e])))}scale(s,e){return new a(this.segments.map(t=>t.map(([r,n])=>[r*s,n*e])))}draw(s){let e=new Path2D;this.segments.forEach(t=>{t.length<2||(e.moveTo(t[0][0],t[0][1]),t.slice(1).forEach(([r,n])=>e.lineTo(r,n)))}),s.stroke(e)}}export{a as P};
